mapper.select:
select u.id,u.merchant_id,u.enabled,u.type,u.username,u.password,u.phone,u.head_url,u.remark,u.`name`, r.id as rId,r.name,r.description from sys_user u LEFT JOIN sys_user_role sur on u.id= sur.sys_user_id LEFT JOIN sys_role r on sur.sys_role_id=r.id where u.id in ( select id from sys_user where merchant_id = #{merchantId} and id != #{userId}
and ( `username` like concat('%',#{keywords},'%') or `name` like concat('%',#{keywords},'%') )
and is_delete = 0 and `type` = 2 ) and sur.is_delete = 0 and r.is_delete = 0
